import React from "react";
import logo from "./logo.png";
import Cart from "./Cart";

const NavigationBar = () => {
  const showCart = () => {
    return (
      <div class="cart-overlay">
        <div class="cart">
          <span class="close-cart">
            <button class="close-cart">Close</button>
          </span>
          <h2>Your cart</h2>
          <div class="cart-content"></div>
          <div class="cart-footer">
            <h3>
              Your total average: â‚¬ <span class="cart-total">0</span>
            </h3>
            <button class="clear-cart banner-btn">Clear grocery list</button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="nav-bar">
      <button className="navigation-btn">
        <i class="fas fa-location-arrow"></i>
      </button>
      <img className="imgs" src={logo} alt="img" />
      <button className="cart-btn" onClick={showCart}>
        <i class="fas fa-shopping-cart"></i>
      </button>
      <div className="cart-items">0</div>
    </div>
  );
};

export default NavigationBar;
