import React from "react";
import { Link } from "react-router-dom";

const Cart = props => {
  let total = 0;

  const removeItem = e => {
    if (e.target.classList.contains("remove-item")) {
      let identification = e.target.dataset.id;
      console.log(identification);

      setCart([props.cart.filter(item => item.id !== identification)]);
    }
  };

  return (
    <div className="cart-overlay">
      <div className="cart">
        <span className="close-cart">
          <Link to="/">
            <button className="close-cart">Close</button>
          </Link>
        </span>
        <h2>Your cart</h2>
        <div className="cart-content">
          <ul>
            {props.cart.map(item => {
              total += item.price;
              console.log(item.id);
              return (
                <li>
                  <h1>{item.title}</h1>
                  <img className="product-img" src={item.img} alt="img" />
                  <h2>€{item.price}</h2>
                  <span
                    class="remove-item"
                    onClick={removeItem}
                    data-id={item.id}
                  >
                    remove
                  </span>
                </li>
              );
            })}
          </ul>
        </div>
        <div className="cart-footer">
          <h3>
            Your total average: €{" "}
            <span className="cart-total">{parseFloat(total.toFixed(2))}</span>
          </h3>
          <button className="clear-cart banner-btn">Clear grocery list</button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
