import React from "react";

const Product = props => {
  const addToCart = e => {
    props.setCart([
      {
        img: props.prod.img,
        title: props.prod.title,
        price: props.prod.price,
      },
    ]);
  };

  return (
    <article className="product">
      <div className="img-container">
        <li>
          <img className="product-img" src={props.prod.img} alt="image" />
          <button className="bag-btn" onClick={addToCart}>
            Add product
          </button>
          <h3>{props.prod.title}</h3>
          <h4>â‚¬{props.prod.price}</h4>
        </li>
      </div>
    </article>
  );
};

export default Product;
