import React from "react";

const Product = props => {
  let identification = e.target.dataset.id;
  const addToCart = () => {
    if (props.prod.id === identification)
      props.setCart([
        ...props.cart,
        {
          id: props.prod.id,
          img: props.prod.img,
          title: props.prod.title,
          price: props.prod.price,
          amount: 1,
        },
      ]);
    props.setCart([
      ...props.cart,
      {
        id: props.prod.id,
        img: props.prod.img,
        title: props.prod.title,
        price: props.prod.price,
        amount: 1,
      },
    ]);
    console.log(props.cart);
    changestate();
  };

  const changestate = () => {
    props.setcartOpen(prev => !prev);
  };

  return (
    <article className="product">
      <div className="img-container">
        <li>
          <img className="product-img" src={props.prod.img} alt="imag" />
          <button
            className="bag-btn"
            data-id={props.prod.id}
            onClick={addToCart}
          >
            Add product
          </button>
          <h3>{props.prod.title}</h3>
          <h4>â‚¬{props.prod.price}</h4>
        </li>
      </div>
    </article>
  );
};

export default Product;
