import React, { useState, useEffect } from "react";
import "./App.css";
import Products from "./components/Products";
import NavigationBar from "./components/NavigationBar";
import GroceryList from "./components/GroceryList";
import Cart from "./components/Cart";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

function App() {
  const [products, setProducts] = useState([]);

  const getProducts = async () => {
    try {
      const result = await fetch("products.json");
      const data = await result.json();
      let products = data.items;
      products = products.map(item => {
        const { title, price } = item.fields;
        const id = item.sys.id;
        const img = item.fields.image.fields.file.url;
        return { title, price, id, img };
      });
      setProducts(products);
      console.log(products);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getProducts();
  }, []);

  return (
    <div className="cart-overlay">
      <div className="cart">
        <span className="close-cart">
          <button className="close-cart">Close</button>
        </span>
        <h2>Your cart</h2>
        <div className="cart-content"></div>
        <div className="cart-footer">
          <h3>
            Your total average: â‚¬ <span className="cart-total">0</span>
          </h3>
          <button className="clear-cart banner-btn">Clear grocery list</button>
        </div>
      </div>
    </div>
  );
}

export default App;
