import React from "react";

import { Link } from "react-router-dom";

const Cart = props => {
  return (
    <div className="cart-overlay">
      <div className="cart">
        <span className="close-cart">
          <Link to="/">
            <button className="close-cart">Close</button>
          </Link>
        </span>
        <h2>Your cart</h2>
        <div className="cart-content">
          <ul>
            {props.cart.map(item => {
              return (
                <li>
                  <h1>{item.title}</h1>
                  <img className="product-img" src={item.img} alt="img" />
                  <h2>{item.price}</h2>
                </li>
              );
            })}
          </ul>
        </div>
        <div className="cart-footer">
          <h3>
            Your total average: â‚¬ <span className="cart-total">0</span>
          </h3>
          <button className="clear-cart banner-btn">Clear grocery list</button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
